import React, { useEffect, useState } from 'react';
import PrimeReact from 'primereact/api';
import { useAsyncMemo } from 'use-async-memo'
import _ from 'lodash';

import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeflex/primeflex.css';

import 'App.css';
import getWeb3 from 'common/getWeb3';
import LotteryPage from 'pages/lottery.page';
import CampaignPage from 'pages/campaign.page';
import MyCtx from 'common/context';
import LoaderPage from 'pages/loader.page';

const App = props => {
  const context = useAsyncMemo(async () => {
    const web3 = await getWeb3();
    const networkId = await web3.eth.net.getId();
    return { web3, networkId };
  }, []);

  useEffect(() => {
    PrimeReact.ripple = true;
    PrimeReact.autoZIndex = true;
    PrimeReact.appendTo = 'self';
    PrimeReact.zIndex = {
      modal: 1100,    // dialog, sidebar
      overlay: 1000,  // dropdown, overlaypanel
      menu: 1000,     // overlay menus
      tooltip: 1100,   // tooltip
      toast: 1200     // toast
    };
  }, [context]);

  if (_.isEmpty(context)) {
    return <LoaderPage />;
  }

  return (
    <MyCtx.Provider value={context}>
      <div class="block p-4">
        <div class="flex card-container indigo-container">
          <div class="flex-1 w-6rem border-round p-4">
            <LotteryPage />
          </div>
          <div class="flex-1 w-6rem border-round p-4">
            <CampaignPage />
          </div>
        </div>
      </div>
    </MyCtx.Provider>
  );
}

export default App;
